def b(n):
    if n <= 1:
        return 1
    total = 0
    i = n
    while i > 0:
        if i % 2 == 0:
            total += b(i//2)
        elif i % 5 == 0:
            total += 3
        else:
            total += 1
        i -= 1
    return total

